import React, { useState } from 'react';

function Form() {
  const [name, setName] = useState('');

  return (
    <form>
        <label htmlFor='name'>
            <input
            type="text"
            id='name'
            value={name}
            onChange={({ target }) => setName(target.name)}
            />
        </label>
    </form>
  );
}

export default Form;
